import PopupComponent from './PopupComponent';

const ContextualPopupComponent = ContextualPopupDecorator(Button);

const MyComponent = kind({
	name: 'MyComponent',

	render: (props) => {
		const popupProps = {
			functionProp: () => {},
			stringProp: '',
			booleanProp: false
		};

		return (
			<div {...props}>
				<ContextualPopupComponent
					popupComponent={PopupComponent}
					popupProps={popupProps}
				>
					Open Popup
				</ContextualPopupComponent>
			</div>
		);
	}
});
